package Array;

public class Forlooparray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a[]= {20,55,63};

for(int i=0;i<=2;i++)
{
System.out.println(a[i]+",");
}
{
	System.out.println("---------");					
}
for(int i=2;i>=0;i--)
{
	System.out.println(a[i]);
}
{
System.out.println("----------");
}
for(int i:a )
{
	System.out.println(i);
}
}
}
